// src/components/TripDetails.js
import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import '../styles/TripDetails.css';

const mockTrips = [
  {
    id: '1',
    title: 'Goa Beach Escape',
    description: 'Relax on sunny beaches and enjoy vibrant nightlife.',
    itinerary: 'Day 1: Arrival\nDay 2: Beach tour\nDay 3: Party night\nDay 4: Departure',
    price: 8999,
    image: 'https://source.unsplash.com/800x400/?beach,goa'
  },
  {
    id: '2',
    title: 'Manali Mountain Adventure',
    description: 'Explore the Himalayan mountains and scenic valleys.',
    itinerary: 'Day 1: Arrival\nDay 2: Rohtang Pass\nDay 3: Mall Road\nDay 4: Departure',
    price: 10999,
    image: 'https://source.unsplash.com/800x400/?mountains,manali'
  },
  {
    id: '3',
    title: 'Kerala Backwaters',
    description: 'Houseboat cruises and lush green landscapes await you.',
    itinerary: 'Day 1: Kochi\nDay 2: Houseboat\nDay 3: Alappuzha\nDay 4: Departure',
    price: 9999,
    image: 'https://source.unsplash.com/800x400/?kerala,backwaters'
  }
];

const TripDetails = () => {
  const { id } = useParams();
  const [trip, setTrip] = useState(null);

  useEffect(() => {
    const foundTrip = mockTrips.find(t => t.id === id);
    setTrip(foundTrip);
  }, [id]);

  if (!trip) {
    return <div className="trip-details-loading">Loading trip details...</div>;
  }

  return (
    <div className="trip-details-container">
      <img src={trip.image} alt={trip.title} className="trip-details-image" />
      <div className="trip-details-content">
        <h2>{trip.title}</h2>
        <p>{trip.description}</p>
        <h4>Itinerary:</h4>
        <pre>{trip.itinerary}</pre>
        <h3>Price: â‚¹{trip.price}</h3>
        <Link to={`/book/${trip.id}`} className="book-btn">Book Now</Link>
      </div>
    </div>
  );
};

export default TripDetails;
