// src/components/BookingForm.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/BookingForm.css';

const mockTrips = [
  { id: '1', title: 'Goa Beach Escape', price: 6999 },
  { id: '2', title: 'Manali Mountain Adventure', price: 8999 },
  { id: '3', title: 'Kerala Backwaters', price: 7999 },
  { id: '4', title: 'Rajasthan Desert Safari', price: 7999 },
  { id: '5', title: 'Manali Snow Retreat', price: 7999 },
  { id: '6', title: 'Andaman Island Explorer', price: 7999 },
  { id: '7', title: 'Meghalaya Cave Expedition', price: 7999 },
  { id: '8', title: 'Kashmir Heaven Tour', price: 7999 },
  { id: '9', title: 'Coorg Coffee Hills Escape', price: 7999 },
  { id: '10', title: 'Sikkim Serenity Tour', price: 7999 },
];

const BookingForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    travelers: 1,
    date: '',
  });

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Booking Data:', formData);
    navigate('/success');
  };

  return (
    <div className="booking-form-container">
      <h2>Book Your Trip</h2>
      <form className="booking-form" onSubmit={handleSubmit}>
        <label>
          Full Name
          <input
            type="text"
            name="name"
            placeholder="Your full name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Email Address
          <input
            type="email"
            name="email"
            placeholder="your@email.com"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Number of Travelers
          <input
            type="number"
            name="travelers"
            min="1"
            max="20"
            value={formData.travelers}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Travel Date
          <input
            type="date"
            name="date"
            value={formData.date}
            onChange={handleChange}
            required
          />
        </label>

        <button type="submit" className="book-trip-btn">Confirm Booking</button>
      </form>
    </div>
  );
};

export default BookingForm;
