import React from 'react';
import '../styles/TripDetails.css';

function TripDetails() {
  const trip = {
    image: 'https://source.unsplash.com/800x400/?nature,travel',
    title: 'Himalayan Adventure',
    days: 7,
    rating: 4.8,
    price: 499,
    description:
      'Experience the ultimate Himalayan adventure with scenic treks, stunning views, and cultural insights. This 7-day tour takes you through majestic landscapes and offers the thrill of a lifetime.',
  };

  return (
    <section className="trip-details">
      <img src={trip.image} alt={trip.title} className="trip-banner" />
      <div className="trip-details-content">
        <h2>{trip.title}</h2>
        <p className="trip-meta">{trip.days} Days | ‚≠ê {trip.rating}/5</p>
        <p className="trip-price">${trip.price}</p>
        <p className="trip-description">{trip.description}</p>
        <a href="/booking" className="trip-book-button">Book Now</a>
      </div>
    </section>
  );
}

export default TripDetails;
