import React from 'react';
import '../styles/TripCard.css';
import { useNavigate } from 'react-router-dom';

function TripCard({ image, title, days, rating, price }) {
  const navigate = useNavigate();

  const handleClick = () => {
    navigate('/trip/' + title.toLowerCase().replace(/\s+/g, '-'));
  };

  return (
    <div className="trip-card" onClick={handleClick}>
      <img src={image} alt={title} className="trip-image" />
      <div className="trip-info">
        <h3>{title}</h3>
        <p>{days} Days Tour</p>
        <p>‚≠ê {rating} / 5</p>
        <p className="trip-price">${price}</p>
      </div>
    </div>
  );
}

export default TripCard;
