// src/components/BookingForm.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/BookingForm.css';

const mockTrips = [
  { id: '1', title: 'Goa Beach Escape', price: 6999 },
  { id: '2', title: 'Manali Mountain Adventure', price: 8999 },
  { id: '3', title: 'Kerala Backwaters', price: 7999 },
  { id: '4', title: 'Rajasthan Desert Safari', price: 7999 },
  { id: '5', title: 'Manali Snow Retreat', price: 7999 },
  { id: '6', title: 'Andaman Island Explorer', price: 7999 },
  { id: '7', title: 'Meghalaya Cave Expedition', price: 7999 },
  { id: '8', title: 'Kashmir Heaven Tour', price: 7999 },
  { id: '9', title: 'Coorg Coffee Hills Escape', price: 7999 },
  { id: '10', title: 'Sikkim Serenity Tour', price: 7999 },
];

const BookingForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [trip, setTrip] = useState(null);
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    travelers: 1,
    date: ''
  });

  useEffect(() => {
    const foundTrip = mockTrips.find(t => t.id === id);
    setTrip(foundTrip);
  }, [id]);

  const handleChange = e => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = e => {
    e.preventDefault();
    console.log('Booking Submitted:', formData);
    navigate('/success');
  };

  if (!trip) return <div className="booking-loading">Loading trip info...</div>;

  return (
    <div className="booking-form-container">
      <h2>Book Your Trip: {trip.title}</h2>
      <form onSubmit={handleSubmit} className="booking-form">
        <label>
          Full Name:
          <input type="text" name="fullName" required value={formData.fullName} onChange={handleChange} />
        </label>
        <label>
          Email Address:
          <input type="email" name="email" required value={formData.email} onChange={handleChange} />
        </label>
        <label>
          Number of Travelers:
          <input type="number" name="travelers" min="1" required value={formData.travelers} onChange={handleChange} />
        </label>
        <label>
          Travel Date:
          <input type="date" name="date" required value={formData.date} onChange={handleChange} />
        </label>
        <button type="submit" className="booking-submit-btn">Confirm Booking</button>
      </form>
    </div>
  );
};

export default BookingForm;
