import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from './AuthContext';
import '../styles/AuthForm.css';

function Signup() {
  const { login } = useAuth();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ name: '', email: '', password: '' });
  const [passwordStrength, setPasswordStrength] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    if (name === 'password') {
      setPasswordStrength(checkPasswordStrength(value));
    }
  };

  const checkPasswordStrength = (password) => {
    if (password.length < 6) return 'Weak';
    if (password.match(/[A-Za-z]/) && password.match(/[0-9]/) && password.length >= 6) return 'Medium';
    if (password.match(/[A-Za-z]/) && password.match(/[0-9]/) && password.match(/[^A-Za-z0-9]/) && password.length >= 8) return 'Strong';
    return 'Weak';
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    login({ email: formData.email, name: formData.name });
    navigate('/');
  };

  return (
    <div className="auth-container">
      <div className="auth-left">
        <div className="auth-left-overlay">
          Start your adventure with TripGo ✈️
        </div>
      </div>

      <div className="auth-right">
        <div className="auth-card">
          <h2>Create Your Account</h2>
          <form onSubmit={handleSubmit}>
            <input name="name" type="text" placeholder="Full Name" required onChange={handleChange} />
            <input name="email" type="email" placeholder="Email Address" required onChange={handleChange} />
            <input name="password" type="password" placeholder="Password" required onChange={handleChange} />
            {formData.password && (
              <div style={{ fontWeight: 'bold', color: getStrengthColor(passwordStrength), marginBottom: '10px' }}>
                Password Strength: {passwordStrength}
              </div>
            )}
            <button type="submit">Sign Up</button>
          </form>
          <p>Already have an account? <Link to="/login">Log In</Link></p>
        </div>
      </div>
    </div>
  );
}

function getStrengthColor(strength) {
  switch (strength) {
    case 'Weak': return 'red';
    case 'Medium': return '#f47c00';
    case 'Strong': return 'green';
    default: return '#333';
  }
}

export default Signup;
