// src/components/BookingForm.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/BookingForm.css';

const demoTrips = [
  {
    id: 1,
    title: 'Goa Beach Getaway',
    image: '/images/goa.jpg',
    price: 6999,
  },
  {
    id: 2,
    title: 'Himalayan Trek Adventure',
    image: '/images/himalaya.jpg',
    price: 8999,
  },
  {
    id: 3,
    title: 'Kerala Backwaters Escape',
    image: '/images/kerala.jpg',
    price: 7999,
  },
  {
    id: 4,
    title: 'Rajasthan Desert Safari',
    image: '/images/rajasthan.jpg',
    price: 7499,
  },
  {
    id: 5,
    title: 'Manali Snow Retreat',
    image: '/images/manali.jpg',
    price: 8299,
  },
  {
    id: 6,
    title: 'Andaman Island Explorer',
    image: '/images/andaman.jpg',
    price: 9999,
  },
  {
    id: 7,
    title: 'Meghalaya Cave Expedition',
    image: '/images/meghalaya.jpg',
    price: 8599,
  },
  {
    id: 8,
    title: 'Kashmir Heaven Tour',
    image: '/images/kashmir.jpg',
    price: 10499,
  },
  {
    id: 9,
    title: 'Coorg Coffee Hills Escape',
    image: '/images/coorg.jpg',
    price: 7699,
  },
  {
    id: 10,
    title: 'Sikkim Serenity Tour',
    image: '/images/sikkim.jpg',
    price: 8899,
  },
];

const BookingForm = () => {
  const { id } = useParams();
  const [trip, setTrip] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const selectedTrip = demoTrips.find(t => t.id === parseInt(id));
    setTrip(selectedTrip);
  }, [id]);

  const handleSubmit = (e) => {
    e.preventDefault();
    alert('Booking successful!');
    navigate('/success');
  };

  if (!trip) return <div className="booking-form-container">Trip not found.</div>;

  return (
    <div className="booking-form-container">
      <h2>Book Your Trip: <span>{trip.title}</span></h2>
      <form className="booking-form" onSubmit={handleSubmit}>
        <input type="text" placeholder="Full Name" required />
        <input type="email" placeholder="Email Address" required />
        <input type="tel" placeholder="Phone Number" required />
        <input type="date" placeholder="Travel Date" required />
        <button type="submit" className="submit-btn">Confirm Booking</button>
      </form>
    </div>
  );
};

export default BookingForm;
