// src/components/TripList.js
import React, { useState, useEffect } from 'react';
import TripCard from './TripCard';
import '../styles/TripList.css';

const TripList = () => {
  const [trips, setTrips] = useState([]);

  // Simulated data fetch (replace with API later)
  useEffect(() => {
    const demoTrips = [
      {
        id: 1,
        title: 'Goa Beach Getaway',
        description: 'Enjoy 3 days of sun, sand, and sea in beautiful Goa.',
        image: '/images/goa.jpg',
        price: 6999,
      },
      {
        id: 2,
        title: 'Himalayan Trek Adventure',
        description: 'Breathtaking views, fresh air, and adventure await you.',
        image: '/images/himalaya.jpg',
        price: 8999,
      },
      {
        id: 3,
        title: 'Kerala Backwaters Escape',
        description: 'Float through paradise in a tranquil houseboat ride.',
        image: '/images/kerala.jpg',
        price: 7999,
      },
    ];
    setTrips(demoTrips);
  }, []);

  return (
    <div className="trip-list-container">
      <h2 className="trip-list-heading">Explore Our Best Trips</h2>
      {trips.length === 0 ? (
        <p className="no-trips">No trips available at the moment.</p>
      ) : (
        <div className="trip-grid">
          {trips.map(trip => (
            <TripCard key={trip.id} trip={trip} />
          ))}
        </div>
      )}
    </div>
  );
};

export default TripList;
