// src/components/AboutUs.js
import React from 'react';
import '../styles/AboutUs.css';

const teamMembers = [
  {
    name: "Vikram Patel",
    role: "Founder & Travel Strategist",
    img: "/images/team1.jpg",
    bio: "Vikram ensures each trip is crafted with precision and passion.",
  },
  {
    name: "Anjali Sharma",
    role: "Customer Experience Head",
    img: "/images/team2.jpg",
    bio: "Anjali is the heart of our customer journey.",
  },
  {
    name: "Rahul Mehta",
    role: "Adventure Coordinator",
    img: "/images/team3.jpg",
    bio: "Rahul makes sure every adventure is safe and unforgettable.",
  },
];

const AboutUs = () => {
  return (
    <div className="about-bg">
      <section className="about-header">
        <h1>About Us</h1>
        <p>We believe travel should be inspiring, unforgettable, and effortless.</p>
      </section>

      <section className="mission-section">
        <h2>Our Mission</h2>
        <p>
          At VM Trips, we strive to create extraordinary travel experiences tailored for every kind of explorer.
          Whether itâ€™s a peaceful retreat or a thrilling adventure, we handle it all.
        </p>
      </section>

      <section className="team-section">
        <h2>Meet the Team</h2>
        <div className="team-grid">
          {teamMembers.map((member, index) => (
            <div className="team-card" key={index}>
              <img src={member.img} alt={member.name} />
              <h3>{member.name}</h3>
              <p className="role">{member.role}</p>
              <p className="bio">{member.bio}</p>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default AboutUs;
