import React from 'react';
import { useParams } from 'react-router-dom';
import '../styles/TripDetails.css';

function TripDetails() {
  const { id } = useParams();

  return (
    <section className="trip-details">
      <img
        src={`https://source.unsplash.com/800x400/?${id}`}
        alt={id}
        className="trip-banner"
      />
      <div className="trip-details-content">
        <h2>{id.replace(/-/g, ' ')}</h2>
        <p className="trip-meta">7 Days | ‚≠ê 4.8/5</p>
        <p className="trip-price">$499</p>
        <p className="trip-description">
          This is a placeholder description for the {id.replace(/-/g, ' ')} trip. You can customize this.
        </p>
        <a href="/booking" className="trip-book-button">
          Book Now
        </a>
      </div>
    </section>
  );
}

export default TripDetails;
