import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from './AuthContext';
import '../styles/Login.css';

function Login() {
  const { login } = useAuth();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ email: '', password: '' });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    login({ email: formData.email });
    navigate('/');
  };

  return (
    <div className="login-page">
      <div className="overlay"></div>
      <div className="login-card">
        <h1 className="logo">Trip<span>Go</span></h1>
        <p className="tagline">Start your journey now üåç</p>
        <form onSubmit={handleSubmit}>
          <input name="email" type="email" placeholder="Email Address" required onChange={handleChange} />
          <input name="password" type="password" placeholder="Password" required onChange={handleChange} />
          <button type="submit">Login</button>
        </form>
        <p className="switch-text">Don‚Äôt have an account? <Link to="/signup">Sign Up</Link></p>
      </div>
    </div>
  );
}

export default Login;
