// src/components/BlogFAQ.js
import React from 'react';
import '../styles/BlogFAQ.css';

const blogPosts = [
  {
    id: 1,
    title: 'Top 5 Beach Destinations in India',
    summary: 'From Goa to Andaman, explore India’s most stunning coastal getaways.'
  },
  {
    id: 2,
    title: 'How to Pack Smart for a Mountain Trip',
    summary: 'Avoid overpacking and learn what’s really essential for a hill station.'
  }
];

const faqs = [
  {
    question: 'How do I cancel a booking?',
    answer: 'You can cancel your booking from the Dashboard under "My Bookings".'
  },
  {
    question: 'Do you offer group discounts?',
    answer: 'Yes, we offer group discounts for 5 or more travelers. Contact support for more info.'
  }
];

const BlogFAQ = () => {
  return (
    <div className="blogfaq-container">
      <section className="blog-section">
        <h2>Travel Blog</h2>
        {blogPosts.map(post => (
          <div key={post.id} className="blog-post">
            <h3>{post.title}</h3>
            <p>{post.summary}</p>
          </div>
        ))}
      </section>

      <section className="faq-section">
        <h2>Frequently Asked Questions</h2>
        {faqs.map((faq, index) => (
          <div key={index} className="faq-item">
            <p className="faq-q"><strong>Q:</strong> {faq.question}</p>
            <p className="faq-a"><strong>A:</strong> {faq.answer}</p>
          </div>
        ))}
      </section>
    </div>
  );
};

export default BlogFAQ;
