// src/components/AboutUs.js
import React, { useState } from 'react';
import '../styles/AboutUs.css';

const tabs = ['Mission', 'Vision', 'Core Values'];

const AboutUs = () => {
  const [activeTab, setActiveTab] = useState('Mission');

  return (
    <div className="about-container">
      <div className="about-header">
        <h1>About Us</h1>
        <p>We believe travel should be inspiring, unforgettable, and effortless.</p>
      </div>

      <div className="about-tabs">
        {tabs.map((tab) => (
          <button
            key={tab}
            className={`tab-btn ${activeTab === tab ? 'active' : ''}`}
            onClick={() => setActiveTab(tab)}
          >
            {tab}
          </button>
        ))}
      </div>

      <div className="tab-content">
        {activeTab === 'Mission' && (
          <div>
            <h2>Our Mission</h2>
            <p>
              At VM Trips, our mission is to make travel accessible, customized, and magical. We craft
              experiences that let travelers explore the world with comfort and confidence — whether it’s a
              luxury retreat, a cultural exploration, or an offbeat adventure.
            </p>
          </div>
        )}

        {activeTab === 'Vision' && (
          <div>
            <h2>Our Vision</h2>
            <p>
              To become the most trusted and loved travel platform — where every journey is more than just a
              destination, it's a story worth sharing.
            </p>
          </div>
        )}

        {activeTab === 'Core Values' && (
          <div>
            <h2>Core Values</h2>
            <ul className="values-list">
              <li><strong>Passion for Travel:</strong> We live and breathe exploration.</li>
              <li><strong>Customer Delight:</strong> Our guests come first, always.</li>
              <li><strong>Innovation:</strong> We embrace technology to deliver better experiences.</li>
              <li><strong>Integrity:</strong> Transparent and ethical in every interaction.</li>
              <li><strong>Sustainability:</strong> Travel with respect to people and planet.</li>
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default AboutUs;
